$id = $_GET['id'];$query = "SELECT infopfen,infopfennum FROM phome_ecms_shop WHERE id=$id";$result = mysql_query($query);

$v = $_GET['v'];$id = $_GET['id'];$query = "UPDATE phome_ecms_shop SET infopfen = infopfen + $v,infopfennum = infopfennum+1 WHERE id=$id";$result = mysql_query($query);$query = "SELECT infopfen,infopfennum FROM phome_ecms_shop WHERE id=$id";$result = mysql_query($query);
