this.jQuery = (((loaderInfo.parameters.jQuery + "('#") + loaderInfo.parameters.id) + "').jPlayer");……        private function init(_arg1:TimerEvent):void{            this.myInitTimer.stop();            if (ExternalInterface.available){                ……                ExternalInterface.call(this.jQuery, "jPlayerFlashEvent", JplayerEvent.JPLAYER_READY, this.extractStatusData(this.commonStatus));            };        }
