public function update($table, $conditions, $row) {		$where = "";		if (empty ( $row ))			return FALSE;		if (is_array ( $conditions )) {			$join = array ();			foreach ( $conditions as $key => $condition ) {				$condition = $this->escape ( $condition );//转义				$join [] = "{$key} = {$condition}";			}			$where = "WHERE " . join ( " AND ", $join );		} else {			if (null != $conditions)				$where = "WHERE " . $conditions;		}		foreach ( $row as $key => $value ) {			$vals [] = "`$key` = '$value'";//未转义，直接带入，注入		}		$values = join ( ", ", $vals );		$sql = "UPDATE " . dbprefix . "{$table} SET {$values} {$where}";				return $this->db->query ( $sql );	}
