public class MainActivity extends Activity {	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		doit();	}	public void doit() {	      try {	            Intent i = new Intent();	            ComponentName componentName = new ComponentName("com.xunlei.downloadprovider", 	            		"com.xunlei.downloadprovider.app.ExternBroadcastReceiver");	            i.setComponent(componentName);	            sendBroadcast(i);	            Thread.sleep(5000);	            	         }	         catch (Exception e) {}	   }}
