public function delmsg() {        $cmid=$_POST['cmid'];        if (is_array($cmid)) {            $cids=implode(',',$cmid);            M('Comments')->where("comment_id IN ($cids) AND (user_id='".$this->my['user_id']."' OR comment_uid='".$this->my['user_id']."')")->delete();        } else if (is_numeric($cmid)) {            M('Comments')->where("comment_id='$cmid' AND (user_id='".$this->my['user_id']."' OR comment_uid='".$this->my['user_id']."')")->delete();        }        echo json_encode(array("ret"=>'success',"tip"=>L('del_comment_success')));    }


