$rand_dir=date("Y-m-d.",time()).strtolower(rands(3));
调用了rands这个函数。
/***取得随机字符**/function rands($length,$strtolower=1) {	$hash = '';	$chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789abcdefghijklmnopqrstuvwxyz';	$max = strlen($chars) - 1;	mt_srand((double)microtime() * 1000000);	for($i = 0; $i < $length; $i++) {		$hash .= $chars[mt_rand(0, $max)];	}	if($strtolower==1){		$hash=strtolower($hash);	}	return $hash;}
