function UpdateCategoryCount($table,$catid=0,$total=0,$where=''){		    			 //更新上级			 $categoryone=$this->GetMysqlOne('*'," ".$this->GetTable('category')." WHERE  catid='$catid' ");									 if($categoryone){											if(!$total){							  $tasknum=$this->IsSQL($table,"WHERE catid='$catid' $where ");						}else{							  $tasknum=$total;						}															$this->Update('category',array('total'=>$tasknum),array(),"WHERE catid='$catid'");									            						$upid=$categoryone['upid'];																	    $categoryarr=$this->GetMysqlOne('*'," ".$this->GetTable('category')." WHERE  catid='$upid' ");																		if($categoryarr){							    								$up=$this->GetMysqlOne(' sum(total) AS totals '," ".$this->GetTable('category')." WHERE  upid='$upid' ");																$this->UpdateCategoryCount($table,$upid,$up['totals']);							 						}												}			 	}
调用了GetMysqlOne，再付出看看GetMysqlOne
public function GetMysqlOne($key='*',$whereif=''){		  	     $sqldata= array();	  	     $query=sprintf('SELECT %s FROM %s',$key,$whereif);	     $sqldata=mysql_fetch_array($this->MysqlQuery($query),MYSQL_ASSOC);	     if (is_array($sqldata)){ 		       return $sqldata;	     }else{	           return false;	     }     }
