function get_post($index = '', $xss_clean = FALSE)	{		if ( ! isset($_POST[$index]) )		{			return $this->get($index, $xss_clean);		}		else		{			return $this->post($index, $xss_clean);		}	}
3.由于CSDJCMS把get_post()函数的第二个参数设置为了TRUE，那就是要进行一个xss_clean的操作。继续跟踪
public function xss_clean($str, $is_image = FALSE)	{		/*		 * Is the string an array?		 *		 */		if (is_array($str))		{			while (list($key) = each($str))			{				$str[$key] = $this->xss_clean($str[$key]);			}			return $str;		}		/*		 * Remove Invisible Characters		 */		$str = remove_invisible_characters($str);		// Validate Entities in URLs		$str = $this->_validate_entities($str);		/*		 * URL Decode		 *		 * Just in case stuff like this is submitted:		 *		 * <a href="http://%77%77%77%2E%67%6F%6F%67%6C%65%2E%63%6F%6D">Google</a>		 *		 * Note: Use rawurldecode() so it does not remove plus signs		 *		 */		$str = rawurldecode($str);//这里有一个decode的操作		/*		 * Convert character entities to ASCII		 *		 * This permits our tests below to work reliably.		 * We only convert entities that are within tags since		 * these are the ones that will pose security problems.		 *		 */		$str = preg_replace_callback("/[a-z]+=([\'\"]).*?\\1/si", array($this, '_convert_attribute'), $str);		$str = preg_replace_callback("/<\w+.*?(?=>|<|$)/si", array($this, '_decode_entity'), $str);		/*		 * Remove Invisible Characters Again!		 */		$str = remove_invisible_characters($str);
