<?php
include_once 'command/CCmdsPolling.php';

$command = isset($_POST['command'])?$_POST['command']:"";
$saveFile = isset($_POST['filename'])?$_POST['filename']:"";
$cmdParam = isset($_POST['cmdParam'])?$_POST['cmdParam']:"";
$cmdParam = trim($cmdParam);
$faultStr = json_encode(array('type'=>'event', 'name'=>'message', 'data'=>array("exception", "", "") ));

//command is null
if(empty($command)){  
    echo $faultStr;
    exit();
}

//exec and get result
$result = array();
$pollingObj = new CCmdsPolling();
if($command == "ping") {
    $result = $pollingObj->getPingInfo($cmdParam, $saveFile);
} else if ($command == "traceroute") {
    $result = $pollingObj->getTracerouteInfo($cmdParam, $saveFile);
} else {
    echo $faultStr;
    exit();
}

//analyse result state and return
if($result['state'] == "exception") {	
    echo $faultStr;
}  else if($result['state'] == "starting")  {		   
       $re_file=$result['result'];
       $filename=$result['filename'];
       $finished=false;
       echo json_encode(array(
                         'type'=>'event',
                         'name'=>'message',
                         'data'=>array("starting", $re_file, $filename)
                        )); 
} else if($result['state'] == "dealing") {
       $re_file=$result['result']; 
       $filename=$result['filename']; 
       echo json_encode(array(
                         'type'=>'event', 
                         'name'=>'message', 
                         'data'=>array("dealing", $re_file, $filename)
                        ));  
} else if($result['state'] == "finished") {
     $re_file=$result['result']; 
     echo json_encode(array(
                        'type'=>'event', 
                        'name'=>'message', 
                        'data'=>array("finished", $re_file, "")
                        )); 
} else { 
     echo json_encode (array(
                         'type'=>'event',
                         'name'=>'message',
                         'data'=>array("unknown", "", "")
                         )); 
}

?> 
