protected void Page_Load(object sender, EventArgs e){    this.v_oid = base.Request["v_oid"]; //没处理    Lebi_OnlinePay onlinePay = Money.GetOnlinePay(this.v_oid); //跟进    if (onlinePay == null)    {        base.Response.Write("系统错误");        base.Response.End();    }    else    {        string userKey = onlinePay.UserKey;        this.v_oid = base.Request["v_oid"];        this.v_pstatus = base.Request["v_pstatus"];        this.v_pstring = base.Request["v_pstring"];        this.v_pmode = base.Request["v_pmode"];        this.v_md5str = base.Request["v_md5str"];        this.v_amount = base.Request["v_amount"];        this.v_moneytype = base.Request["v_moneytype"];        this.remark1 = base.Request["remark1"];        this.remark2 = base.Request["remark2"];        if (FormsAuthentication.HashPasswordForStoringInConfigFile(this.v_oid + this.v_pstatus + this.v_amount + this.v_moneytype + userKey, "md5").ToUpper() == this.v_md5str)        {            base.Response.Write("ok");            if (this.v_pstatus.Equals("20"))            {                Order.OnlinePaySuccess(this.v_oid, "", false);            }        }        else        {            base.Response.Write("error");        }    }}

public static Lebi_OnlinePay GetOnlinePay(string code){    return GetOnlinePay(B_Lebi_Order.GetModel("Code='" + code + "'")); //存在注入}
